public class Main {
    public static void main(String[] args) {
        int[] n = {8, 15, 3, 6, 12, 5};
        p(n);
        p(n);
    }
    public static void p(int[] n) {
        int i = 0, j;
        while (i < n.length - 1 && (j = 0) < n.length - 1) do {
            n[j] = (n[j] * 3 - n[i]);
        } while (j++ < n.length - 1 && ++i < n.length - 1);
        i = 0;
        while (i < n.length) System.out.print(n[i++] + " ");
    }
}

public class Main {
    public static void main(String[] args) {
        String[] fruits = {"Apples,", "Oranges,", "Bananas,", "Grapes,", "Pineapples,", "Mangoes."};
        int[] quantities = {1, 0, 0, 1, 4, 2};
        process(fruits, quantities);
        display(quantities, fruits);
    }
    public static int[] process(String[] items, int[] counts) {
        int i = 0, j = 0;
        while (i < items.length - 1) {
            j = 0;
            do {
                counts[j] = (counts[j] - counts[i]) * 2;
            } while (j++ < counts.length - 1);
            i++;
        }
        return counts;
    }
    public static void display(int[] counts, String[] items) {
        int i = 0;
        while (i < counts.length) {
            System.out.print(counts[i] + " " + items[i]);
            i++;
        }
    }
}

public class Main {
    public static void main(String[] args) {
        int n = 5;
        patternCipher(n);
    }
    static void patternCipher(int size) {
        for (int i = 1; i <= size; i++) {
            int current = i;
            for (int j = 1; j <= i; j++) {
                System.out.print(current + " ");
                current += i - j;
            }
            System.out.println();
        }
    }
}

public class Main {
    public static void main(String[] args) {
        int[] a = {5, 3, 7};
        int[] r = new int[2];
        r[0] = calc(a, true);
        r[1] = calc(a, false);
        display(r);
    }
    static int calc(int[] in, boolean first) {
        int i = first ? 0 : 1;
        int j = first ? 1 : 0;
        return (in[i] + in[j]) * in[2]--;
    }
    static void display(int[] result) {
        System.out.println("Result: ");
        for (int v : result) {
            System.out.print(v + " ");
        }
    }
}
