public class Main {
    public static void main(String[] a) {
        int[] b1 = {1, 2, 3, 4, 5};
        int[] b2 = new int[b1.length];
        for (int i = 0; i < b1.length; i++) {
            b2[i] = b1[i] * 2;
            if (i % 2 == 0) {
                b2[i] = b1[i] + 3;
            } else {
                b2[i] = b1[i] - 1;
                for (int j = 0; j < b2.length; j++) {
                    if (b2[j] > 0) {
                        b2[j] += i;
                    }
                }
            }
        }
        System.out.print("H: ");
        for (int n : b1) {
            System.out.print(n + " ");
        }
        System.out.print("\nI: ");
        for (int n : b2) {
            System.out.print(n + " ");
        }
    }
}

public class Main {
    public static void main(String[] a) {
        int b1 = 6;
        int b = 4;
        int c = 0;
        if ((b1 > 5 && b > 2) || (b < 4 && b1 > 3 && (b1 + b) < 10)) {
            c = b1 * 2;
            while (c > 0) {
                System.out.print(c + " ");
                c--;
            }
        } else if (b < 4 || (b1 > 3 && (b1 - b) > 1)) {
            c = b * 3;
            do {
                System.out.print(c + " ");
                c--;
            } while (c > 0);
        } else {
            c = b1 + b;
        }
        System.out.println(c);
    }
}
